import { EmptyState, Layout, Page } from "@shopify/polaris";
import {ResourcePicker} from '@shopify/app-bridge-react';

function Index() {
    const [modal, setModal] = React.useState({open: false});

    return (
        <Page>
            <ResourcePicker
            resourceType="Product"
            showVariants={false}
            open={modal.open}
            >
                <Layout
                heading="Mange your inventory transfers"
                actions={{
                    content: 'select product'
                }}
                image={<img src="https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg" alt="empty state" />}
                >
                    <p>Select products</p>
                </Layout>
            </ResourcePicker>
        </Page>
    )
}
